import axios from 'axios';

//  const apiUrl = process.env.REACT_APP_API_URL;
//  const apiUrl ="http://localhost:5116";
 import Config from './config.js'; // יבוא של קובץ ההגדרות

 const apiUrl = Config.apiUrl; // השתמש בכתובת ה־API מתוך ה־Config Defaults
// const apiUrl = process.env.REACT_APP_DEFAULT_API_URL;
//const apiUrl = process.env.REACT_APP_API_URL;/////////////
// process.env.REACT_APP_API_URL
// REACT_APP_API_URL=https://taskserver-ur83.onrender.com

//axios.defaults.baseURL = "https://localhost:7271";
// S

// // Alter defaults after instance has been created
// instance.defaults.headers.common['Authorization'] = AUTH_TOKEN;
axios.interceptors.response.use(
  function (response) {
    return response;
  },
  function (error) {
    console.error('Request failed:', error.message);
    return Promise.reject(error);

  }
);
export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/todos`)    
    return result.data;
  },

  addTask: async(name)=>{
    await axios.post(`${apiUrl}/todo`, { name: name, isComplete: false });
    console.log('addTask', name)
    //TODO
    // return {};
  },

  setCompleted: async(id, isComplete)=>{
    var i = await axios.get(`${apiUrl}/todo/${id}`);
    await axios.put(`${apiUrl}/todos/${id}`, { name: i.data.name, isComplete: isComplete });

    // console.log('setCompleted', {id, isComplete})
    // //TODO
    // return {};
  },

  deleteTask:async(id)=>{
    // console.log('deleteTask')
    await axios.delete(`${apiUrl}/todo/${id}`);

  }
};
