export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/todos`)    
    return result.data;
  },

  addTask: async(name)=>{
    await axios.post(`${apiUrl}/items`, { name: name, isComplete: false });
    console.log('addTask', name)
    //TODO
    // return {};
  },

  setCompleted: async(id, isComplete)=>{
    var i = await axios.get(`${apiUrl}/todo/${id}`);
    await axios.put(`${apiUrl}/todo/${id}`, { name: i.data.name, isComplete: isComplete });

    // console.log('setCompleted', {id, isComplete})
    // //TODO
    // return {};
  },

  deleteTask:async(id)=>{
    // console.log('deleteTask')
    await axios.delete(`/todo/${id}`);

  }
};
